var socket;

socket = io.connect(window.location.hostname);

$("#createChannel").on("click", function() {
  var chat, email, username;
  username = $("#name").val();
  email = $("#email").val();
  if (username === '' || email === '') {
    return false;
  }
  chat = new Chat(username, socket, "chatContainer");
  new Localisation(socket, chat, "map_canvas");
});

